import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';

const PhotoUpload = () => {
  const { reportId } = useParams();
  const { authFetch } = useContext(AuthContext);
  const navigate = useNavigate();

  const [report, setReport] = useState(null);
  const [property, setProperty] = useState(null);
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Form durumları
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [previewUrls, setPreviewUrls] = useState([]);
  const [room, setRoom] = useState('');
  const [damageDescription, setDamageDescription] = useState('');
  const [location, setLocation] = useState('');

  // Rapor bilgilerini ve mevcut fotoğrafları yükle
  useEffect(() => {
    const fetchReportAndPhotos = async () => {
      try {
        // Rapor bilgilerini getir
        const reportResponse = await authFetch(`http://localhost:8080/api/reports/${reportId}`);

        if (!reportResponse.ok) {
          throw new Error('Rapor bilgileri alınamadı');
        }

        const reportData = await reportResponse.json();
        setReport(reportData);
        setProperty(reportData.property);

        // Mevcut fotoğrafları getir
        const photosResponse = await authFetch(`http://localhost:8080/api/reports/${reportId}/photos`);

        if (!photosResponse.ok) {
          throw new Error('Fotoğraf bilgileri alınamadı');
        }

        const photosData = await photosResponse.json();
        setPhotos(photosData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchReportAndPhotos();
  }, [authFetch, reportId]);

  // Dosya seçildiğinde önizleme oluştur
  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(files);

    // Önizleme URL'leri oluştur
    const newPreviewUrls = files.map(file => URL.createObjectURL(file));
    setPreviewUrls(newPreviewUrls);
  };

  // Fotoğraf yükleme
  const handleUpload = async (e) => {
    e.preventDefault();

    if (selectedFiles.length === 0) {
      setError('Lütfen en az bir fotoğraf seçin');
      return;
    }

    if (!room) {
      setError('Lütfen oda/bölüm bilgisini girin');
      return;
    }

    setError('');
    setSuccess('');
    setUploading(true);

    try {
      const formData = new FormData();
      selectedFiles.forEach(file => {
        formData.append('photos', file);
      });

      formData.append('room', room);
      if (damageDescription) formData.append('damageDescription', damageDescription);
      if (location) formData.append('location', location);

      const response = await authFetch(`http://localhost:8080/api/reports/${reportId}/photos`, {
        method: 'POST',
        body: formData,
        // Content-Type header'ı otomatik olarak multipart/form-data olarak ayarlanacaktır
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Fotoğraf yüklenirken bir hata oluştu');
      }

      const newPhotos = await response.json();

      // Yüklenen fotoğrafları ekle ve formu sıfırla
      setPhotos(prevPhotos => [...prevPhotos, ...newPhotos]);
      setSelectedFiles([]);
      setPreviewUrls([]);
      setRoom('');
      setDamageDescription('');
      setLocation('');

      setSuccess('Fotoğraflar başarıyla yüklendi');

      // Önizleme URL'lerini temizle
      previewUrls.forEach(url => URL.revokeObjectURL(url));
    } catch (err) {
      setError(err.message);
    } finally {
      setUploading(false);
    }
  };

  // Raporu tamamla ve ev sahibine gönder
  const handleCompleteReport = async () => {
    if (photos.length === 0) {
      setError('Lütfen rapor için en az bir fotoğraf yükleyin');
      return;
    }

    setError('');
    setSuccess('');

    try {
      const response = await authFetch(`http://localhost:8080/api/reports/${reportId}/send`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          tenantName: localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')).name : 'Kiracı'
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Rapor gönderilirken bir hata oluştu');
      }

      navigate('/dashboard');
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) {
    return <div className="text-center py-8">Yükleniyor...</div>;
  }

  return (
    <div>
      <h1 className="text-2xl font-bold mb-6">Fotoğraf Yükle</h1>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {success && (
        <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
          {success}
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Sol taraf - Rapor ve Mülk Bilgileri */}
        <div className="md:col-span-1">
          <div className="card mb-6">
            <h2 className="text-lg font-medium mb-3">Rapor Bilgileri</h2>
            {property && (
              <>
                <p className="text-gray-800 mb-1">{property.address}</p>
                <p className="text-gray-600 text-sm mb-3">{property.city}</p>
              </>
            )}

            <div className="mt-4">
              <p className="text-sm text-gray-600">
                Rapor Durumu: <span className="font-medium">{report?.status === 'draft' ? 'Taslak' : (report?.status === 'sent' ? 'Gönderildi' : 'Görüntülendi')}</span>
              </p>
              <p className="text-sm text-gray-600">
                Yüklenen Fotoğraf: <span className="font-medium">{photos.length}</span>
              </p>
            </div>
          </div>

          <div className="card">
            <h2 className="text-lg font-medium mb-3">Nasıl Fotoğraf Çekilmeli?</h2>
            <ul className="text-sm text-gray-600 space-y-2">
              <li>Odanın genel görünümünü içeren geniş açılı fotoğraflar çekin</li>
              <li>Mevcut hasarları yakından ve net olarak çekin</li>
              <li>İyi aydınlatılmış ortamlarda çekim yapın</li>
              <li>Her odadaki önemli noktaları mutlaka belgelendirin</li>
              <li>Kapı, pencere, duvar, zemin ve tavanları kontrol edin</li>
            </ul>
          </div>
        </div>

        {/* Sağ taraf - Fotoğraf Yükleme Formu */}
        <div className="md:col-span-2">
          <div className="card mb-6">
            <form onSubmit={handleUpload}>
              <div className="mb-4">
                <label htmlFor="photos" className="form-label">Fotoğraf Seç</label>
                <input
                  type="file"
                  id="photos"
                  accept="image/*"
                  multiple
                  onChange={handleFileSelect}
                  className="w-full p-2 border border-gray-300 rounded-md"
                />
              </div>

              {previewUrls.length > 0 && (
                <div className="mb-4">
                  <p className="form-label">Önizleme:</p>
                  <div className="grid grid-cols-2 sm:grid-cols-3 gap-2">
                    {previewUrls.map((url, index) => (
                      <div key={index} className="aspect-square overflow-hidden rounded-md">
                        <img 
                          src={url} 
                          alt={`Önizleme ${index + 1}`} 
                          className="w-full h-full object-cover"
                        />
                      </div>
                    ))}
                  </div>
                </div>
              )}

              <div className="mb-4">
                <label htmlFor="room" className="form-label">Oda/Bölüm*</label>
                <select
                  id="room"
                  value={room}
                  onChange={(e) => setRoom(e.target.value)}
                  required
                  className="input-field"
                >
                  <option value="">Seçiniz</option>
                  <option value="Salon">Salon</option>
                  <option value="Mutfak">Mutfak</option>
                  <option value="Yatak Odası">Yatak Odası</option>
                  <option value="Banyo">Banyo</option>
                  <option value="Tuvalet">Tuvalet</option>
                  <option value="Koridor">Koridor</option>
                  <option value="Balkon">Balkon</option>
                  <option value="Diğer">Diğer</option>
                </select>
              </div>

              <div className="mb-4">
                <label htmlFor="damageDescription" className="form-label">Hasar Açıklaması (Varsa)</label>
                <textarea
                  id="damageDescription"
                  value={damageDescription}
                  onChange={(e) => setDamageDescription(e.target.value)}
                  className="input-field h-24"
                  placeholder="Fotoğraftaki hasarı detaylı olarak açıklayın"
                ></textarea>
              </div>

              <div className="mb-4">
                <label htmlFor="location" className="form-label">Konum Detayı (İsteğe Bağlı)</label>
                <input
                  type="text"
                  id="location"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  className="input-field"
                  placeholder="Örn: Sağ duvar, mutfak tezgahı, banyo kapısı"
                />
              </div>

              <button
                type="submit"
                disabled={uploading || selectedFiles.length === 0}
                className="btn-primary w-full"
              >
                {uploading ? 'Yükleniyor...' : 'Fotoğrafları Yükle'}
              </button>
            </form>
          </div>

          {/* Yüklenen Fotoğraflar */}
          <div className="card">
            <h2 className="text-lg font-medium mb-3">Yüklenen Fotoğraflar</h2>

            {photos.length === 0 ? (
              <p className="text-gray-600 text-center py-4">Henüz fotoğraf yüklenmedi</p>
            ) : (
              <>
                <div className="grid grid-cols-2 sm:grid-cols-3 gap-3 mb-6">
                  {photos.map((photo) => (
                    <div key={photo.id} className="relative group">
                      <div className="aspect-square overflow-hidden rounded-md">
                        <img 
                          src={`http://localhost:8080${photo.path}`} 
                          alt={photo.originalName} 
                          className="w-full h-full object-cover"
                        />
                      </div>
                      <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-30 transition-all flex items-end p-2">
                        <div className="text-white text-xs opacity-0 group-hover:opacity-100 transition-opacity">
                          <p className="font-medium">{photo.room}</p>
                          {photo.location && <p>{photo.location}</p>}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>

                <div className="flex justify-between items-center">
                  <p className="text-sm text-gray-600">Toplam {photos.length} fotoğraf</p>

                  {report?.status === 'draft' && (
                    <button
                      type="button"
                      onClick={handleCompleteReport}
                      className="btn-primary"
                    >
                      Raporu Tamamla ve Gönder
                    </button>
                  )}
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PhotoUpload;
