import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';

const ReportCreate = () => {
  const { propertyId } = useParams();
  const { authFetch } = useContext(AuthContext);
  const navigate = useNavigate();

  const [property, setProperty] = useState(null);
  const [additionalNotes, setAdditionalNotes] = useState('');
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');

  // Walkthrough aşamasını izlemek için state 
  const [walkthrough, setWalkthrough] = useState({
    started: false,
    currentStep: 0,
    rooms: []
  });

  // Mülk bilgilerini getir
  useEffect(() => {
    const fetchProperty = async () => {
      try {
        const response = await authFetch(`http://localhost:8080/api/properties`);

        if (!response.ok) {
          throw new Error('Mülk bilgileri alınamadı');
        }

        const properties = await response.json();
        const foundProperty = properties.find(p => p.id == propertyId);

        if (!foundProperty) {
          throw new Error('Mülk bulunamadı');
        }

        setProperty(foundProperty);

        // Mülk bilgilerine göre odaları oluştur
        const roomCount = foundProperty.roomCount || 1;
        const bathroomCount = foundProperty.bathroomCount || 1;

        // Standart odalar
        const roomsList = [
          { id: 'living-room', name: 'Salon', completed: false },
          { id: 'kitchen', name: 'Mutfak', completed: false },
        ];

        // Oda sayısına göre yatak odalarını ekle
        for (let i = 1; i <= roomCount; i++) {
          roomsList.push({ id: `bedroom-${i}`, name: `Yatak Odası ${i}`, completed: false });
        }

        // Banyo sayısına göre banyoları ekle
        for (let i = 1; i <= bathroomCount; i++) {
          roomsList.push({ id: `bathroom-${i}`, name: `Banyo ${i}`, completed: false });
        }

        // Diğer alanları ekle
        roomsList.push({ id: 'hallway', name: 'Koridor', completed: false });
        if (foundProperty.amenities) {
          try {
            const amenitiesArray = JSON.parse(foundProperty.amenities);
            if (amenitiesArray.includes('Balkon')) {
              roomsList.push({ id: 'balcony', name: 'Balkon', completed: false });
            }
          } catch (e) {
            console.error("Amenities JSON parse error:", e);
          }
        }

        setWalkthrough(prev => ({ ...prev, rooms: roomsList }));
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProperty();
  }, [authFetch, propertyId]);

  // Walkthrough başlat
  const startWalkthrough = () => {
    setWalkthrough(prev => ({ ...prev, started: true, currentStep: 0 }));
  };

  // Sonraki adıma geç
  const nextRoom = () => {
    if (walkthrough.currentStep < walkthrough.rooms.length - 1) {
      setWalkthrough(prev => ({ 
        ...prev, 
        currentStep: prev.currentStep + 1,
        rooms: prev.rooms.map((room, i) => 
          i === prev.currentStep ? { ...room, completed: true } : room
        )
      }));
    }
  };

  // Önceki adıma git
  const prevRoom = () => {
    if (walkthrough.currentStep > 0) {
      setWalkthrough(prev => ({ ...prev, currentStep: prev.currentStep - 1 }));
    }
  };

  // Belirli bir odaya git
  const goToRoom = (index) => {
    if (index >= 0 && index < walkthrough.rooms.length) {
      setWalkthrough(prev => ({ ...prev, currentStep: index }));
    }
  };

  // Formu gönder ve fotoğraf yükleme sayfasına geç
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSubmitting(true);

    try {
      // Walkthrough bilgilerini JSON olarak ekle
      const roomsData = JSON.stringify(walkthrough.rooms);

      const response = await authFetch(`http://localhost:8080/api/properties/${propertyId}/reports`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ 
          additionalNotes,
          walkthrough: roomsData
        })
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.message || 'Rapor oluşturulurken bir hata oluştu');
      }

      const newReport = await response.json();
      navigate(`/reports/${newReport.id}/photos`);
    } catch (err) {
      setError(err.message);
      setSubmitting(false);
    }
  };

  if (loading) {
    return <div className="text-center py-8">Yükleniyor...</div>;
  }

  return (
    <div>
      <h1 className="text-2xl font-bold mb-6">Yeni Taşınma Raporu Oluştur</h1>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="card max-w-2xl mx-auto">
        {property && (
          <div className="mb-6">
            <h2 className="text-lg font-medium mb-3">Mülk Bilgileri</h2>
            <p className="text-gray-800 mb-1">{property.address}</p>
            <p className="text-gray-600 text-sm mb-3">{property.city}</p>

            <div className="bg-gray-50 p-3 rounded">
              <p className="text-sm font-medium mb-1">Ev Sahibi:</p>
              <p className="text-gray-800 mb-1">{property.landlordName}</p>
              <p className="text-gray-600 text-sm">{property.landlordEmail}</p>
            </div>
          </div>
        )}

        {!walkthrough.started ? (
          <div>
            <div className="mb-6">
              <label htmlFor="additionalNotes" className="form-label">Ek Notlar (İsteğe Bağlı)</label>
              <textarea
                id="additionalNotes"
                value={additionalNotes}
                onChange={(e) => setAdditionalNotes(e.target.value)}
                className="input-field h-32"
                placeholder="Mülk ile ilgili genel durumu veya önemli detayları buraya ekleyebilirsiniz"
              ></textarea>
            </div>

            <div className="bg-blue-50 border-l-4 border-blue-400 p-4 mb-6">
              <div className="flex">
                <div className="ml-3">
                  <p className="text-sm text-blue-700 font-medium mb-1">
                    Daire İnceleme Süreci
                  </p>
                  <p className="text-sm text-blue-700">
                    Dairenizi oda oda inceleyecek ve her alan için fotoğraf yüklemeniz istenecektir. Daire bilgilerinize göre {walkthrough.rooms.length} alan için fotoğraf ve hasar notu ekleyebileceksiniz.
                  </p>
                </div>
              </div>
            </div>

            <div className="flex justify-end">
              <button 
                type="button" 
                onClick={() => navigate('/dashboard')}
                className="btn-secondary mr-3"
              >
                İptal
              </button>
              <button 
                type="button" 
                onClick={startWalkthrough} 
                className="btn-primary"
              >
                İncelemeye Başla
              </button>
            </div>
          </div>
        ) : (
          <form onSubmit={handleSubmit}>
            <div className="mb-6">
              <h2 className="text-lg font-medium mb-4">İnceleme Adımları</h2>

              <div className="flex overflow-x-auto space-x-2 pb-2 mb-4">
                {walkthrough.rooms.map((room, index) => (
                  <button
                    key={room.id}
                    type="button"
                    onClick={() => goToRoom(index)}
                    className={`whitespace-nowrap px-3 py-1 rounded-full text-sm ${walkthrough.currentStep === index 
                      ? 'bg-primary-600 text-white' 
                      : room.completed 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-gray-100 text-gray-800'}`}
                  >
                    {room.name}
                  </button>
                ))}
              </div>

              <div className="p-4 border rounded-lg mb-6">
                <h3 className="font-medium text-lg mb-2">
                  {walkthrough.rooms[walkthrough.currentStep]?.name || 'Oda'}
                </h3>
                <p className="text-gray-600 mb-4">
                  Bu alanın genel durumunu belgelendirin. Duvarlar, zemin, tavan ve önemli detayları kontrol edin.
                </p>

                <div className="bg-yellow-50 border-l-4 border-yellow-400 p-3 mb-4">
                  <p className="text-sm text-yellow-700">
                    Bu bölümü tamamladıktan sonra fotoğraf yükleme aşamasına geçeceksiniz.
                  </p>
                </div>
              </div>

              <div className="flex justify-between">
                <button 
                  type="button" 
                  onClick={prevRoom}
                  disabled={walkthrough.currentStep === 0}
                  className={`px-4 py-2 rounded ${walkthrough.currentStep === 0 ? 'bg-gray-200 text-gray-500 cursor-not-allowed' : 'bg-gray-200 text-gray-800 hover:bg-gray-300'}`}
                >
                  Önceki
                </button>

                {walkthrough.currentStep < walkthrough.rooms.length - 1 ? (
                  <button 
                    type="button" 
                    onClick={nextRoom}
                    className="px-4 py-2 rounded bg-blue-500 text-white hover:bg-blue-600"
                  >
                    Sonraki
                  </button>
                ) : (
                  <button 
                    type="submit" 
                    disabled={submitting} 
                    className="px-4 py-2 rounded bg-green-500 text-white hover:bg-green-600"
                  >
                    {submitting ? 'Tamamlanıyor...' : 'İncelemeyi Tamamla'}
                  </button>
                )}
              </div>
            </div>
          </form>
        )}
      </div>
    </div>
  );
};

export default ReportCreate;
